function DonorDetails_js() {

    /* Object & array with components "name-to-id" mapping */
    var n2id_buf = {
        'mobilegrid_2': 'DonorDetails_mobilegrid_2',
        'mobilegridcell_3': 'DonorDetails_mobilegridcell_3',
        'mobilelabel_7': 'DonorDetails_mobilelabel_7',
        'mobilegridcell_4': 'DonorDetails_mobilegridcell_4',
        'inputfname': 'DonorDetails_inputfname',
        'mobilegridcell_5': 'DonorDetails_mobilegridcell_5',
        'lbllname': 'DonorDetails_lbllname',
        'mobilegridcell_6': 'DonorDetails_mobilegridcell_6',
        'inputlname': 'DonorDetails_inputlname',
        'mobilegridcell_8': 'DonorDetails_mobilegridcell_8',
        'lbldob': 'DonorDetails_lbldob',
        'mobilegridcell_9': 'DonorDetails_mobilegridcell_9',
        'mobiletextinput_44': 'DonorDetails_mobiletextinput_44',
        'mobilegridcell_10': 'DonorDetails_mobilegridcell_10',
        'lblbloodgroup': 'DonorDetails_lblbloodgroup',
        'mobilegridcell_11': 'DonorDetails_mobilegridcell_11',
        'mobiletextinput_45': 'DonorDetails_mobiletextinput_45',
        'mobilegridcell_12': 'DonorDetails_mobilegridcell_12',
        'lbladdress': 'DonorDetails_lbladdress',
        'mobilegridcell_13': 'DonorDetails_mobilegridcell_13',
        'mobiletextinput_46': 'DonorDetails_mobiletextinput_46',
        'mobilegridcell_14': 'DonorDetails_mobilegridcell_14',
        'lblarea': 'DonorDetails_lblarea',
        'mobilegridcell_15': 'DonorDetails_mobilegridcell_15',
        'mobiletextinput_47': 'DonorDetails_mobiletextinput_47',
        'mobilegridcell_16': 'DonorDetails_mobilegridcell_16',
        'lblpincode': 'DonorDetails_lblpincode',
        'mobilegridcell_17': 'DonorDetails_mobilegridcell_17',
        'mobiletextinput_48': 'DonorDetails_mobiletextinput_48',
        'mobilegridcell_18': 'DonorDetails_mobilegridcell_18',
        'lblmobile': 'DonorDetails_lblmobile',
        'mobilegridcell_19': 'DonorDetails_mobilegridcell_19',
        'mobiletextinput_49': 'DonorDetails_mobiletextinput_49',
        'mobilegridcell_20': 'DonorDetails_mobilegridcell_20',
        'lbllandline': 'DonorDetails_lbllandline',
        'mobilegridcell_21': 'DonorDetails_mobilegridcell_21',
        'mobiletextinput_50': 'DonorDetails_mobiletextinput_50',
        'mobilegridcell_22': 'DonorDetails_mobilegridcell_22',
        'lblemail': 'DonorDetails_lblemail',
        'mobilegridcell_23': 'DonorDetails_mobilegridcell_23',
        'mobilegridcell_24': 'DonorDetails_mobilegridcell_24',
        'lblstatus': 'DonorDetails_lblstatus',
        'mobilegridcell_25': 'DonorDetails_mobilegridcell_25',
        'mobiletextinput_51': 'DonorDetails_mobiletextinput_51',
        'mobilegridcell_26': 'DonorDetails_mobilegridcell_26',
        'lblisactive': 'DonorDetails_lblisactive',
        'mobilegridcell_27': 'DonorDetails_mobilegridcell_27',
        'mobiletextinput_52': 'DonorDetails_mobiletextinput_52',
        'btncall': 'DonorDetails_btncall',
        'btnsms': 'DonorDetails_btnsms'
    };

    if ("n2id" in window && window.n2id !== undefined) {
        $.extend(n2id, n2id_buf);
    } else {
        window.n2id = n2id_buf;
    }

    /*
     * Nonvisual components
     */

    Apperyio.mappings = Apperyio.mappings || {};

    Apperyio.datasources = Apperyio.datasources || {};

    Apperyio.CurrentScreen = 'DonorDetails';
    _.chain(Apperyio.mappings).filter(function(m) {
        return m.homeScreen === Apperyio.CurrentScreen;
    }).each(Apperyio.UIHandler.hideTemplateComponents);

    /*
     * Events and handlers
     */

    // On Load
    var DonorDetails_onLoad = function() {
            DonorDetails_elementsExtraJS();

            DonorDetails_deviceEvents();
            DonorDetails_windowEvents();
            DonorDetails_elementsEvents();
        };

    // screen window events


    function DonorDetails_windowEvents() {

        $('#DonorDetails').bind('pageshow orientationchange', function() {
            var _page = this;
            adjustContentHeightWithPadding(_page);
        });

    };

    // device events


    function DonorDetails_deviceEvents() {
        document.addEventListener("deviceready", function() {

        });
    };

    // screen elements extra js


    function DonorDetails_elementsExtraJS() {
        // screen (DonorDetails) extra code

    };

    // screen elements handler


    function DonorDetails_elementsEvents() {
        $(document).on("click", "a :input,a a,a fieldset label", function(event) {
            event.stopPropagation();
        });

        $(document).off("click", '#DonorDetails_mobilecontainer [name="btncall"]').on({
            click: function(event) {
                if (!$(this).attr('disabled')) {
                    window.open('tel:+91' + Apperyio('lblnumber').text().trim(), '_system');

                }
            },
        }, '#DonorDetails_mobilecontainer [name="btncall"]');
        $(document).off("click", '#DonorDetails_mobilecontainer [name="btnsms"]').on({
            click: function(event) {
                if (!$(this).attr('disabled')) {
                    window.open('sms:+91' + Apperyio('lblnumber').text().trim() + '?body=Hello from Appery.io!', '_system');

                }
            },
        }, '#DonorDetails_mobilecontainer [name="btnsms"]');

    };

    $(document).off("pagebeforeshow", "#DonorDetails").on("pagebeforeshow", "#DonorDetails", function(event, ui) {
        Apperyio.CurrentScreen = "DonorDetails";
        _.chain(Apperyio.mappings).filter(function(m) {
            return m.homeScreen === Apperyio.CurrentScreen;
        }).each(Apperyio.UIHandler.hideTemplateComponents);
    });

    DonorDetails_onLoad();
};

$(document).off("pagecreate", "#DonorDetails").on("pagecreate", "#DonorDetails", function(event, ui) {
    Apperyio.processSelectMenu($(this));
    DonorDetails_js();
});