/*
 * JS for startScreen generated by Appery.io
 */

Apperyio.getProjectGUID = function() {
    return 'abc8a72b-bfc3-4bec-8e50-92928c6286f5';
};

function navigateTo(outcome, useAjax) {
    Apperyio.navigateTo(outcome, useAjax);
}

function adjustContentHeight() {
    Apperyio.adjustContentHeightWithPadding();
}

function adjustContentHeightWithPadding(_page) {
    Apperyio.adjustContentHeightWithPadding(_page);
}

function setDetailContent(pageUrl) {
    Apperyio.setDetailContent(pageUrl);
}

Apperyio.AppPages = [{
    "name": "DonorDetails",
    "location": "DonorDetails.html"
}, {
    "name": "startScreen",
    "location": "startScreen.html"
}, {
    "name": "Register",
    "location": "Register.html"
}];

function startScreen_js() {

    /* Object & array with components "name-to-id" mapping */
    var n2id_buf = {
        'inputpin': 'startScreen_inputpin',
        'mobilebutton_53': 'startScreen_mobilebutton_53',
        'mobilelist_4': 'startScreen_mobilelist_4',
        'mobilelistitem_5': 'startScreen_mobilelistitem_5',
        'mobilelistitembutton_6': 'startScreen_mobilelistitembutton_6',
        'mobilegrid_11': 'startScreen_mobilegrid_11',
        'mobilegridcell_12': 'startScreen_mobilegridcell_12',
        'lblfname': 'startScreen_lblfname',
        'lblbloodgroup': 'startScreen_lblbloodgroup',
        'lblpincode': 'startScreen_lblpincode',
        'lbllastdonated': 'startScreen_lbllastdonated',
        'lblmobile': 'startScreen_lblmobile',
        'mobilebutton_31': 'startScreen_mobilebutton_31',
        'mobilegridcell_13': 'startScreen_mobilegridcell_13',
        'showname': 'startScreen_showname',
        'showbloodgroup': 'startScreen_showbloodgroup',
        'showpincode': 'startScreen_showpincode',
        'showlastdonated': 'startScreen_showlastdonated',
        'showmobile': 'startScreen_showmobile',
        'mobilebutton_33': 'startScreen_mobilebutton_33',
        'btbregisterdonor': 'startScreen_btbregisterdonor'
    };

    if ("n2id" in window && window.n2id !== undefined) {
        $.extend(n2id, n2id_buf);
    } else {
        window.n2id = n2id_buf;
    }

    /*
     * Nonvisual components
     */

    Apperyio.mappings = Apperyio.mappings || {};

    Apperyio.mappings["startScreen_restservicedonorlist_onsuccess_mapping_0"] = {
        "homeScreen": "startScreen",
        "directions": [

        {
            "from_name": "restservicedonorlist",
            "from_type": "SERVICE_RESPONSE",

            "to_name": "startScreen",
            "to_type": "UI",

            "mappings": [

            {

                "source": "$['body'][i]['bloodgroup']",
                "target": "$['mobilegrid_11']['showbloodgroup:text']"

            },

            {

                "source": "$['body'][i]",
                "target_transformation": function(value, element) {
                    return '{"bloodgroup":"' + value + '"}';
                },
                "target": "$['mobilegrid_11']"

            },

            {

                "source": "$['body'][i]['fname']",
                "target": "$['mobilegrid_11']['showname:text']"

            },

            {

                "source": "$['body'][i]['pincode']",
                "target": "$['mobilegrid_11']['showpincode:text']"

            },

            {

                "source": "$['body'][i]['lastdonationdate']",
                "target": "$['mobilegrid_11']['showlastdonated:text']"

            },

            {

                "source": "$['body'][i]['mobilenumber']",
                "target": "$['mobilegrid_11']['showmobile:text']"

            }

            ]
        }

        ]
    };

    Apperyio.mappings["startScreen_restservicedonorlist_onbeforesend_mapping_0"] = {
        "homeScreen": "startScreen",
        "directions": [

        {
            "from_name": "startScreen",
            "from_type": "UI",

            "to_name": "restservicedonorlist",
            "to_type": "SERVICE_REQUEST",

            "to_default": {
                "headers": {
                    "X-Appery-Database-Id": "{database_id}"
                },
                "parameters": {
                    "where": "{\"bloodgroup\":\"O+\" , \"pincode\":\"400601\"}"
                },
                "body": null
            },

            "mappings": [

            {

                "source": "$['inputpin:text']",
                "target_transformation": function(value) {
                    return '{"pincode": "' + value + '"}';
                },
                "target": "$['parameters']['where']"

            }

            ]
        }

        ]
    };

    Apperyio.datasources = Apperyio.datasources || {};

    window.restservicedonorlist = Apperyio.datasources.restservicedonorlist = new Apperyio.DataSource(NGODB_DonorDetails_list_service, {
        "onBeforeSend": function(jqXHR) {
            Apperyio.processMappingAction(Apperyio.mappings["startScreen_restservicedonorlist_onbeforesend_mapping_0"]);
        },
        "onComplete": function(jqXHR, textStatus) {

            Apperyio.refreshScreenFormElements("startScreen");
        },
        "onSuccess": function(data) {
            Apperyio.processMappingAction(Apperyio.mappings["startScreen_restservicedonorlist_onsuccess_mapping_0"]);
        },
        "onError": function(jqXHR, textStatus, errorThrown) {}
    });

    Apperyio.CurrentScreen = 'startScreen';
    _.chain(Apperyio.mappings).filter(function(m) {
        return m.homeScreen === Apperyio.CurrentScreen;
    }).each(Apperyio.UIHandler.hideTemplateComponents);

    /*
     * Events and handlers
     */

    // On Load
    var startScreen_onLoad = function() {
            startScreen_elementsExtraJS();

            startScreen_deviceEvents();
            startScreen_windowEvents();
            startScreen_elementsEvents();
        };

    // screen window events


    function startScreen_windowEvents() {

        $('#startScreen').bind('pageshow orientationchange', function() {
            var _page = this;
            adjustContentHeightWithPadding(_page);
        });

    };

    // device events


    function startScreen_deviceEvents() {
        document.addEventListener("deviceready", function() {

        });
    };

    // screen elements extra js


    function startScreen_elementsExtraJS() {
        // screen (startScreen) extra code

        /* mobilelist_4 */

        listView = $("#startScreen_mobilelist_4");
        theme = listView.attr("data-theme");
        if (typeof theme !== 'undefined') {
            var themeClass = "ui-btn-up-" + theme;
            listItem = $("#startScreen_mobilelist_4 .ui-li-static");
            $.each(listItem, function(index, value) {
                $(this).addClass(themeClass);
            });
        }

        /* mobilelistitem_5 */

    };

    // screen elements handler


    function startScreen_elementsEvents() {
        $(document).on("click", "a :input,a a,a fieldset label", function(event) {
            event.stopPropagation();
        });

        $(document).off("click", '#startScreen_mobilecontainer [name="mobilebutton_53"]').on({
            click: function(event) {
                if (!$(this).attr('disabled')) {
                    try {
                        restservicedonorlist.execute({});
                    } catch (e) {
                        console.error(e);
                        hideSpinner();
                    };

                }
            },
        }, '#startScreen_mobilecontainer [name="mobilebutton_53"]');

        $(document).off("click", '#startScreen_mobilecontainer [name="mobilelistitem_5"]').on({
            click: function(event) {
                if (!$(this).attr('disabled')) {
                    Apperyio.navigateTo('DonorDetails', {
                        reverse: false
                    });

                }
            },
        }, '#startScreen_mobilecontainer [name="mobilelistitem_5"]');

        $(document).off("click", '#startScreen_mobilecontainer [name="mobilebutton_31"]').on({
            click: function(event) {
                if (!$(this).attr('disabled')) {
                    window.open('tel:+91' + Apperyio('showmobile').text().trim(), '_system');

                }
            },
        }, '#startScreen_mobilecontainer [name="mobilebutton_31"]');

        $(document).off("click", '#startScreen_mobilecontainer [name="mobilebutton_33"]').on({
            click: function(event) {
                if (!$(this).attr('disabled')) {
                    window.open('sms:+91' + Apperyio('showmobile').text().trim() + '?body=Hello', '_system');

                }
            },
        }, '#startScreen_mobilecontainer [name="mobilebutton_33"]');
        $(document).off("click", '#startScreen_mobilecontainer [name="btbregisterdonor"]').on({
            click: function(event) {
                if (!$(this).attr('disabled')) {
                    Apperyio.navigateTo('Register', {
                        reverse: false
                    });

                }
            },
        }, '#startScreen_mobilecontainer [name="btbregisterdonor"]');

    };

    $(document).off("pagebeforeshow", "#startScreen").on("pagebeforeshow", "#startScreen", function(event, ui) {
        Apperyio.CurrentScreen = "startScreen";
        _.chain(Apperyio.mappings).filter(function(m) {
            return m.homeScreen === Apperyio.CurrentScreen;
        }).each(Apperyio.UIHandler.hideTemplateComponents);
    });

    startScreen_onLoad();
};

$(document).off("pagecreate", "#startScreen").on("pagecreate", "#startScreen", function(event, ui) {
    Apperyio.processSelectMenu($(this));
    startScreen_js();
});